                'limit': '',
                'originalPrice': '￥11.00',
                'pic': 'http://11.url.cn/huayang/resource/yule/new_gift/ilivenew/memeda91.png'
            },
            'i1': {
                'btnText': '兑换',
                'count': 'x55',
                'currentPrice': '￥8.00',
                'isExist': true,
                'limit': '',
                'originalPrice': '￥8.00',
                'pic': 'http://11.url.cn/huayang/resource/yule/new_gift/ilivenew/ainio91.png'
            },
            'i2': {
                'btnText': '兑换',
